pragma solidity ^0.4.24;

import "./zeppelin/ownership/Ownable.sol";
import "./DataStorageBridge.sol";
import "./DeployerBridge.sol";

/**
 * The permanent contract on the chain, to rule them all.
 */
contract Overlord is Ownable, DataStorageBridge, DeployerBridge {
    /**
     * Deployer contract address.
     */
    address private deployer;
    /**
     * Storage contract address
     */
    address private dataStorage;

    /**
     * Notify that deployer contract has changed.
     */
    event DeployerChanged(
        address indexed oldDeployer,
        address indexed newDeployer
    );

    /**
     * Notify that deployer contract has changed.
     */
    event DataStorageChanged(
        address indexed oldDataStorage,
        address indexed newDataStorage
    );

    /**
     * Change the deployer contract address.
     */
    function setDeployer(address _deployer) external onlyOwner {
        emit DeployerChanged(deployer, _deployer);
        deployer = _deployer;
    }

    /**
     * Get the deployer contract address.
     */
    function getDeployer() external view returns (address) {
        return deployer;
    }


    /**
     * Change the data storage contract address.
     */
    function setDataStorage(address _dataStorage) external onlyOwner {
        emit DataStorageChanged(dataStorage, _dataStorage);
        dataStorage = _dataStorage;
    }

    /**
     * Get the data storage contract address.
     */
    function getDataStorage() external view returns (address) {
        return dataStorage;
    }

    /**
     * Don't allow arbitrary calls
     */
    function() public {
        revert();
    }
}
